import { select } from "d3-selection";
import React, { useEffect, useRef } from "react";
import styled from "styled-components";

const LegendWrapper = styled.div`
  display: flex;
  align-items: center;
  font-family: 'Bennett Text', Georgia, serif;
  gap: 1rem;
`;

const LegendText = styled.div`
  font-size: 14px;
  color: #2a4b5c;
  max-width: 150px;
`;

const Svg = styled.svg`
  overflow: visible;
`;

export default function CircleLegend({ sizeScale }) {
  const ref = useRef();

  useEffect(() => {
    const svg = select(ref.current);
    svg.selectAll("*").remove();

    const allCounts = data.flatMap(d => d.categories.map(c => c.count));
    const min = d3.min(allCounts);
    const max = d3.max(allCounts);
    const mid = Math.round((min + max) / 2);

    const values = [min, mid, max];
    const spacing = 2;
    const circleX = 50;
    const labelX = circleX;
    const lastY = 80;

    const circles = values
      .slice()
      .sort((a, b) => sizeScale(b) - sizeScale(a));

    circles.forEach((val, i) => {
      const r = sizeScale(val);
      const cy = lastY - r;

      svg
        .append("circle")
        .attr("cx", circleX)
        .attr("cy", cy)
        .attr("r", r)
        .attr("fill", "#abc2d1")
        .attr("fill-opacity", 0.4)
        .attr("stroke", "#abc2d1");

      svg
        .append("text")
        .attr("x", labelX)
        .attr("y", cy)
        .attr("dy", "0.35em")
        .attr("text-anchor", "middle")
        .text(val)
        .style("font-size", "12px")
        .style("fill", "#2a4b5c")
        .style("font-weight", "600");
    });
  }, [sizeScale]);

  return (
    <LegendWrapper>
      <LegendText>
        <div style={{ fontWeight: 600, fontSize: "16px" }}>TARGETS</div>
        <div style={{ fontSize: "14px", color: "#444" }}>
          The larger the circle, the more targets.
        </div>
      </LegendText>
      <Svg ref={ref} width={100} height={100} />
    </LegendWrapper>
  );
}
