import './App.css';
import LineChart from './LineChart';
import { GlobalStyles } from './GlobalStyles';
import CategoryBubbleChart from './CategoryBubbleChart';
import dataAll from "./data_all.json";
import dataRaqqa from "./data_raqqa.json";
import dataMosul from "./data_mosul.json";
import { scaleLinear } from 'd3-scale';
import styled from 'styled-components';
import mapUrl from './assets/map_group.svg';
import Vector from "./assets/Vector.svg";

const RightPanel = styled.div`
  display: flex;
  flex-direction: column;s
  width: 570px;
`;

const MapContainer = styled.div`
margin: 10px 10px 10px 10px;
position: relative;
`;

const MosulIndicator = styled.img`
  position:absolute;
  top:136px;
  left: 373px;
`;

const MosulText = styled.div`
  position:absolute;
  top:198px;
  left: 372px;
  color: #156082;
text-align: center;
font-size: 13px;
font-style: normal;
font-weight: 600;
line-height: normal;
`;

const RaqqaIndicator = styled.img`
  position:absolute;
  top:118px;
  left: 296px;
`;

const RaqqaText = styled.div`
  position:absolute;
top:179px;
  left: 294px;
  color: #156082;
text-align: center;
font-size: 13px;
font-style: normal;
font-weight: 600;
line-height: normal;
`;

const ContentContainer = styled.div`
  display: flex;
  justify-content: center;
  width:1200px;
  margin: auto;
`;

const AppContainer = styled.div`
  display: flex;
`;
function App() {

  const allCounts = [...dataAll, ...dataRaqqa, ...dataMosul].flatMap(d => d.categories.map(c => c.count));
  const minCount = Math.min(...allCounts);
  const maxCount = Math.max(...allCounts);

  const sizeScale = scaleLinear()
    .domain([minCount, maxCount])
    .range([8, 150]);
  return (
    <AppContainer className="App">
      <ContentContainer>
        <MapContainer>
          <img height={700} src={mapUrl} alt="map" />
          <MosulIndicator height={70} width={40} src={Vector} alt="map" />
          <MosulText>Mosul</MosulText>
          <RaqqaIndicator height={70} width={40} src={Vector} alt="map" />
          <RaqqaText>Raqqa</RaqqaText>
        </MapContainer>

        <RightPanel>
          <LineChart data={dataAll} />
          <CategoryBubbleChart data={dataAll} sizeScale={sizeScale} />
        </RightPanel>
      </ContentContainer>
    </AppContainer>
  );
}

export default App;
