import React from 'react';
import styled from 'styled-components';
import data from './mosul_targets_by_quarter.json';

const GridWrapper = styled.div`
  padding: 2rem;
  font-family: 'Bennett Text', Georgia, serif;
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const CategoryRow = styled.div`
  display: flex;
  flex-direction: column;
`;

const CategoryLabel = styled.div`
  font-size: 20px;
  font-weight: 500;
  margin-bottom: 0.5rem;
`;

const QuarterRow = styled.div`
  display: flex;
  align-items: flex-end;
  gap: 6px;
`;

const CircleContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 50px;
`;

const CircleStack = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const BlurredCircle = styled.div`
  position: absolute;
  border-radius: 50%;
  background-color: ${({ color }) => color};
  opacity: 0.7;
  filter: blur(3px);
  width: ${({ size }) => size}px;
  height: ${({ size }) => size}px;
`;

const OutlineCircle = styled.div`
  position: absolute;
  border-radius: 50%;
  border: 1px solid ${({ color }) => color};
  width: ${({ size }) => size}px;
  height: ${({ size }) => size}px;
`;

const CenterDot = styled.div`
  position: absolute;
  width: 6px;
  height: 6px;
  background-color: ${({ color }) => color};
  border-radius: 50%;
  z-Index: 1;
`;

const QuarterLabel = styled.div`
  font-size: 10px;
  text-align: center;
  margin-top: 0.25rem;
`;

const CountLabel = styled.div`
  font-size: 10px;
  margin-top: 2px;
  color: #333;
`;

const colorMap = {
  "Military Personnel, Equipment & Facilities": "#DFC5B2",
  "Vehicles": "#B7BCD6",
  "Transportation Infrastructure": "#ACBCC5",
  "Residential Buildings": "#5088A1",
  "Manufacturing, Production, and Construction": "#998C84",
  "Oil Infrastructure": "#D2D9B6",
  "Media and Telecom Facilities": "#9194A8",
  "Terrain": "#AEAC8D",
  "Water Infrastructure": "#8EBADB",
  "Electrical Infrastructure": "#78A898",
  "Financial and Banking Facilities": "#98D5D3"
};

const allCounts = data.flatMap(d => d.categories.map(c => c.count));
const minCount = Math.min(...allCounts);
const maxCount = Math.max(...allCounts);

const scaleSize = (count) => {
  const minSize = 8;
  const maxSize = 80;
  return ((count - minCount) / (maxCount - minCount)) * (maxSize - minSize) + minSize;
};

export default function CategoryBubbleChart() {
  const categories = Array.from(
    new Set(data.flatMap(d => d.categories.map(c => c.name)))
  );

  return (
    <GridWrapper>
      {categories.map((category) => (
        <CategoryRow key={category}>
          <CategoryLabel>{category}</CategoryLabel>
          <QuarterRow>
            {data.map((d) => {
              const found = d.categories.find(c => c.name === category);
              const count = found ? found.count : 0;
              const size = scaleSize(count);
              const label = d.quarter.split(" ");
              const color = colorMap[category] || '#ccc';
              return (
                <CircleContainer key={`${category}-${d.quarter}`}>
                  {size !== 0 && <>
                    <QuarterLabel>{label[0]}</QuarterLabel>
                    <QuarterLabel>{label[1]}</QuarterLabel>
                    <CircleStack>
                      <BlurredCircle size={size} color={color} />
                      <OutlineCircle size={size} color={color} />
                      <CenterDot color={color} />
                    </CircleStack>
                  </>
                  }
                  <CountLabel>{count}</CountLabel>
                </CircleContainer>
              );
            })}
          </QuarterRow>
        </CategoryRow>
      ))}
    </GridWrapper>
  );
}
