import React, { useEffect, useRef } from 'react';
import { select } from 'd3-selection';
import { scalePoint, scaleLinear, scaleOrdinal } from 'd3-scale';
import { max } from 'd3-array';
import { line } from 'd3-shape';
import { axisLeft, axisBottom } from 'd3-axis';
import styled from 'styled-components';
import data from './mosul_targets_by_quarter.json';

const ChartContainer = styled.div`
  width: 100%;
  height: 600px;
  font-family: 'Georgia', serif;
  color: #333;
`;

const Legend = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  margin-bottom: 1rem;
  font-size: 14px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;

  &::before {
    content: '';
    display: inline-block;
    width: 20px;
    height: 2px;
    margin-right: 6px;
    background-color: ${(props) => props.color || '#000'};
  }
`;

export default function LineChart() {
  const svgRef = useRef();

  useEffect(() => {
    const svg = select(svgRef.current);
    svg.selectAll('*').remove();

    const margin = { top: 20, right: 20, bottom: 60, left: 60 };
    const width = 900 - margin.left - margin.right;
    const height = 500 - margin.top - margin.bottom;

    const g = svg
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const quarters = data.map((d) => d.quarter);
    const categories = Array.from(new Set(data.flatMap((d) => d.categories.map((c) => c.name))));

    const colorMap = {
      "Military Personnel, Equipment & Facilities": "#DFC5B2",
      "Vehicles": "#B7BCD6",
      "Transportation Infrastructure": "#ACBCC5",
      "Residential Buildings": "#5088A1",
      "Manufacturing, Production, and Construction": "#998C84",
      "Oil Infrastructure": "#D2D9B6",
      "Media and Telecom Facilities": "#9194A8",
      "Terrain": "#AEAC8D",
      "Water Infrastructure": "#8EBADB",
      "Electrical Infrastructure": "#78A898",
      "Financial and Banking Facilities": "#98D5D3"
    };

    const xScale = scalePoint()
      .domain(quarters)
      .range([0, width]);

    const yMax = max(data, (d) => {
      return max(d.categories, (c) => c.count);
    });

    const yScale = scaleLinear()
      .domain([0, yMax])
      .range([height, 0]);

    const color = scaleOrdinal()
      .domain(categories)
      .range(categories.map(cat => colorMap[cat] || '#ccc'));

    g.append('g')
      .call(axisLeft(yScale));

    g.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(axisBottom(xScale))
      .selectAll('text')
      .style('text-anchor', 'middle')
      .attr('dy', null)
      .html(function (_, i) {
        const [q, y] = quarters[i].split(" ");
        return `${q}\n${y}`;
      });

    categories.forEach((category) => {
      const lineData = data.map((d) => {
        const entry = d.categories.find((c) => c.name === category);
        return {
          quarter: d.quarter,
          count: entry ? entry.count : 0
        };
      });

      const line = line()
        .x((d) => xScale(d.quarter))
        .y((d) => yScale(d.count));

      g.append('path')
        .datum(lineData)
        .attr('fill', 'none')
        .attr('stroke', color(category))
        .attr('stroke-width', 1.5)
        .attr('d', line);
    });
  }, []);

  const categories = Array.from(new Set(data.flatMap(d => d.categories.map(c => c.name))));

  const colorMap = {
    "Military Personnel, Equipment & Facilities": "#DFC5B2",
    "Vehicles": "#B7BCD6",
    "Transportation Infrastructure": "#ACBCC5",
    "Residential Buildings": "#5088A1",
    "Manufacturing, Production, and Construction": "#998C84",
    "Oil Infrastructure": "#D2D9B6",
    "Media and Telecom Facilities": "#9194A8",
    "Terrain": "#AEAC8D",
    "Water Infrastructure": "#8EBADB",
    "Electrical Infrastructure": "#78A898",
    "Financial and Banking Facilities": "#98D5D3"
  };

  return (
    <ChartContainer>
      <Legend>
        {categories.map((name) => (
          <LegendItem key={name} color={colorMap[name] || '#ccc'}>
            {name}
          </LegendItem>
        ))}
      </Legend>
      <svg ref={svgRef}></svg>
    </ChartContainer>
  );
}
