import React, { useEffect, useRef } from 'react';
import { select, selectAll } from 'd3-selection';
import { scalePoint, scaleLinear } from 'd3-scale';
import { max } from 'd3-array';
import { line as d3Line, curveMonotoneX } from 'd3-shape';
import { transition } from 'd3-transition';
import { group } from 'd3-array';
import styled from 'styled-components';

const Title = styled.div`
  color: #000;
  text-align: center;
  font-size: 16px;
  font-weight: 400;
  margin: 10px;
`;

const ChartContainer = styled.div`
  width: 570px;
  color: #333;
  font-family: 'Bennett Text', Georgia, serif;
`;

const Legend = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-bottom: 1rem;
  font-size: 12px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;

  &::before {
    content: '';
    display: inline-block;
    width: 20px;
    height: 2px;
    margin-right: 6px;
    background-color: ${(props) => props.color || '#000'};
  }
`;

export default function LineChart({ data, name }) {
  const svgRef = useRef();

  const colorMap = {
    "Military Personnel, Equipment & Facilities": "#DFC5B2",
    "Vehicles": "#B7BCD6",
    "Transportation Infrastructure": "#ACBCC5",
    "Residential Buildings": "#5088A1",
    "Manufacturing, Production, and Construction": "#998C84",
    "Oil Infrastructure": "#D2D9B6",
    "Media and Telecom Facilities": "#9194A8",
    "Terrain": "#AEAC8D",
    "Water Infrastructure": "#8EBADB",
    "Electrical Infrastructure": "#78A898",
    "Financial and Banking Facilities": "#98D5D3"
  };

  const sortedCategories = Array.from(
    new Set(data.flatMap((d) => d.categories.map((c) => c.name)))
  ).sort((a, b) => {
    const totalA = data.reduce((sum, d) => {
      const item = d.categories.find((c) => c.name === a);
      return sum + (item ? item.count : 0);
    }, 0);
    const totalB = data.reduce((sum, d) => {
      const item = d.categories.find((c) => c.name === b);
      return sum + (item ? item.count : 0);
    }, 0);
    return totalB - totalA;
  });

  const quarters = data.map((d) => d.quarter);

  const color = (category) => colorMap[category] || '#ccc';

  const xScale = scalePoint().domain(quarters).range([0, 570 - 60 - 20]);
  const yMax = max(data, (d) => max(d.categories, (c) => c.count));
  const yScale = scaleLinear().domain([0, yMax + 200]).range([500 - 5 - 60, 0]);

  const lineGen = d3Line()
    .x((d) => xScale(d[0]))
    .y((d) => yScale(d[1]))
    .curve(curveMonotoneX);

  useEffect(() => {
    const svg = select(svgRef.current);

    svg.attr('width', 570).attr('height', 500);

    svg
      .append('g')
      .attr('id', 'data-paths')
      .attr('fill', 'none')
      .attr('stroke-width', 3)
      .attr('stroke-linejoin', 'round')
      .attr('stroke-linecap', 'round');
  }, []);

  useEffect(() => {
    const t = transition().duration(500).ease(d3.easeLinear);

    const groups = group(
      sortedCategories.flatMap((category) => [
        data.map((d) => {
          const entry = d.categories.find((c) => c.name === category);
          return [d.quarter, entry ? entry.count : 0, category];
        }),
      ]),
      (d) => d[0][2]
    );

    selectAll('#data-paths')
      .selectAll('path')
      .data(groups.values())
      .join('path')
      .attr('stroke', (d) => color(d[0][2]))
      .attr('stroke-width', '1.5px')
      .transition(t)
      .attr('d', lineGen);
  }, [data]);

  return (
    <ChartContainer>
      <Title>
        Targets, By Total Number in Press Releases per Quarter in{' '}
        <span style={{ color: '#156082', fontWeight: 600 }}>{name}</span>
      </Title>
      <Legend>
        {sortedCategories.map((name) => (
          <LegendItem key={name} color={colorMap[name] || '#ccc'}>
            {name}
          </LegendItem>
        ))}
      </Legend>
      <svg ref={svgRef}></svg>
    </ChartContainer>
  );
}
