import React, { useEffect, useRef } from 'react';
import { select } from 'd3-selection';
import { scalePoint, scaleLinear } from 'd3-scale';
import { max } from 'd3-array';
import { axisLeft, axisBottom } from 'd3-axis';
import { line, area } from 'd3-shape';
import styled from 'styled-components';

const ChartContainer = styled.div`
  width: 600px;
  height: 600px;
  color: #333;
  font-family: 'Bennett Text', Georgia, serif;
`;

const Legend = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  margin-bottom: 1rem;
  font-size: 14px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;

  &::before {
    content: '';
    display: inline-block;
    width: 20px;
    height: 2px;
    margin-right: 6px;
    background-color: ${(props) => props.color || '#000'};
  }
`;

export default function LineChart(props) {
  const { data } = props;
  const svgRef = useRef();

  const colorMap = {
    "Military Personnel, Equipment & Facilities": "#DFC5B2",
    "Vehicles": "#B7BCD6",
    "Transportation Infrastructure": "#ACBCC5",
    "Residential Buildings": "#5088A1",
    "Manufacturing, Production, and Construction": "#998C84",
    "Oil Infrastructure": "#D2D9B6",
    "Media and Telecom Facilities": "#9194A8",
    "Terrain": "#AEAC8D",
    "Water Infrastructure": "#8EBADB",
    "Electrical Infrastructure": "#78A898",
    "Financial and Banking Facilities": "#98D5D3"
  };


  const sortedCategories = Array.from(
    new Set(data.flatMap(d => d.categories.map(c => c.name)))
  ).sort((a, b) => {
    const totalA = data.reduce((sum, d) => {
      const item = d.categories.find(c => c.name === a);
      return sum + (item ? item.count : 0);
    }, 0);
    const totalB = data.reduce((sum, d) => {
      const item = d.categories.find(c => c.name === b);
      return sum + (item ? item.count : 0);
    }, 0);
    return totalB - totalA;
  });
  //.filter((category) => Object.keys(colorMap).includes(category));

  useEffect(() => {
    const svg = select(svgRef.current);
    svg.selectAll('*').remove();

    const margin = { top: 20, right: 0, bottom: 60, left: 60 };
    const width = 600 - margin.left - margin.right;
    const height = 500 - margin.top - margin.bottom;



    const g = svg
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    const defs = svg.append('defs');
    const quarters = data.map((d) => d.quarter);

    const colorMap = {
      "Military Personnel, Equipment & Facilities": "#DFC5B2",
      "Vehicles": "#B7BCD6",
      "Transportation Infrastructure": "#ACBCC5",
      "Residential Buildings": "#5088A1",
      "Manufacturing, Production, and Construction": "#998C84",
      "Oil Infrastructure": "#D2D9B6",
      "Media and Telecom Facilities": "#9194A8",
      "Terrain": "#AEAC8D",
      "Water Infrastructure": "#8EBADB",
      "Electrical Infrastructure": "#78A898",
      "Financial and Banking Facilities": "#98D5D3"
    };

    const xScale = scalePoint().domain(quarters).range([0, width]);

    const yMax = max(data, (d) => max(d.categories, (c) => c.count));
    const yScale = scaleLinear().domain([0, yMax]).range([height, 0]);

    g.append('g')
      .call(axisLeft(yScale).ticks(5).tickSize(-width).tickPadding(8))
      .call(g => g.select('.domain').remove())
      .call(g => g.selectAll('line').attr('stroke', '#D9D9D9').attr('stroke-width', 0.5))
      .call(g => g.selectAll('text').attr('x', -12).attr("color", "#595959").attr("font-size", 12));

    const xAxis = g.append('g')
      .attr('transform', `translate(0,${height})`)
      .call(axisBottom(xScale))
      .call(g => g.select('.domain').remove())
      .call(g => g.selectAll('line').attr('stroke', '#BCBCBC').attr('stroke-opacity', 0.35))
      .call(g => g.selectAll('text').remove());

    // Custom stacked quarter/year text labels
    const xAxisGroup = g.append('g')
      .attr('transform', `translate(0,${height})`);

    quarters.forEach((q, i) => {
      const [quarter, year] = q.split(" ");
      const x = xScale(q);
      if (x != null) {
        const group = xAxisGroup.append("g")
          .attr("transform", `translate(${x},0)`);

        group.append("text")
          .attr("y", 22)
          .attr("text-anchor", "middle")
          .attr("font-size", 14)
          .style("fill", "#595959")
          .text(quarter);

        group.append("text")
          .attr("y", 37)
          .attr("font-size", 14)
          .attr("text-anchor", "middle")
          .style("fill", "#595959")
          .text(year);
      }
    });

    sortedCategories.forEach((category, idx) => {
      const lineData = data.map((d) => {
        const entry = d.categories.find((c) => c.name === category);
        return {
          quarter: d.quarter,
          count: entry ? entry.count : 0
        };
      });

      const id = `grad-${idx}`;
      const color = colorMap[category] || '#ccc';

      const gradient = defs.append('linearGradient')
        .attr('id', id)
        .attr('x1', '0%')
        .attr('y1', '0%')
        .attr('x2', '0%')
        .attr('y2', '100%');

      gradient.append('stop')
        .attr('offset', '0%')
        .attr('stop-color', color)
        .attr('stop-opacity', 0.6);

      gradient.append('stop')
        .attr('offset', '100%')
        .attr('stop-color', '#ffffff')
        .attr('stop-opacity', 0.8);

      const areaPath = area()
        .x((d) => xScale(d.quarter))
        .y0(height)
        .y1((d) => yScale(d.count));

      g.append('path')
        .datum(lineData)
        .attr('fill', `url(#${id})`)
        .attr('fill-opacity', 0.7)
        .attr('stroke', 'none')
        .attr('d', areaPath);

      const linePath = line()
        .x((d) => xScale(d.quarter))
        .y((d) => yScale(d.count));

      g.append('path')
        .datum(lineData)
        .attr('fill', 'none')
        .attr('stroke', color)
        .attr('stroke-width', 1.5)
        .attr('d', linePath);
    });
  }, []);

  return (
    <ChartContainer>
      <Legend>
        {sortedCategories.map((name) => (
          <LegendItem key={name} color={colorMap[name] || '#ccc'}>
            {name}
          </LegendItem>
        ))}
      </Legend>
      <svg ref={svgRef}></svg>
    </ChartContainer>
  );
}
