import './App.css';
import LineChart from './LineChart';
import { GlobalStyles } from './GlobalStyles';
import CategoryBubbleChart from './CategoryBubbleChart';
import dataAll from "./data_all.json";
import dataRaqqa from "./data_raqqa.json";
import dataMosul from "./data_mosul.json";
import { scaleLinear } from 'd3-scale';
import styled from 'styled-components';
import mapUrl from './assets/map_group.svg';
import Vector from "./assets/Vector.svg";
import { useState } from 'react';
import backgroudUrl from "./assets/background.svg";

const RightPanel = styled.div`
  display: flex;
  flex-direction: column;
  width: 570px;
`;

const IndicatorContainer = styled.div`
  display: flex;
  flex-direction: column;
  cursor: pointer;
`;

const MapContainer = styled.div`
margin: 10px 10px 10px 10px;
    position: sticky;
    top: 0;
    height: 100%;
`;

const MosulIndicator = styled.img`
  position:absolute;
  top:136px;
  left: 373px;
`;

const MosulText = styled.div`
  position:absolute;
  top:198px;
  left: 372px;
  color: #156082;
text-align: center;
font-size: 13px;
font-style: normal;
font-weight: 600;
line-height: normal;
`;

const RaqqaIndicator = styled.img`
  position:absolute;
  top:118px;
  left: 296px;
`;

const RaqqaText = styled.div`
  position:absolute;
top:179px;
  left: 294px;
  color: #156082;
text-align: center;
font-size: 13px;
font-style: normal;
font-weight: 600;
line-height: normal;
`;

const ContentContainer = styled.div`
  display: flex;
  justify-content: center;
  width:1200px;
  margin: auto;
  z-Index: 2;
  background: #ffffff
`;

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const AppHeader = styled.div`
height: 400px;
width: 100%;
background: rgba(115, 169, 193, 0.85);
z-Index: 0;
position: relative;
`;

const Circle = styled.div`
border-radius: 50%;
width: 100%;
height: 400px;
position: absolute;
top: 0;
left: 0;
background: radial-gradient(#156082, #8FC2D8);
filter: blur(20px);
opacity: .6;
`;

const HeaderText = styled.div`
display: flex;
position: absolute;
top: 75px;
justify-content: center;
width: 100%;
`;

const ArticleTitle = styled.div`
font-family: 'Bennett Text', Georgia, serif;
color: #FFF;
text-align: center;
font-size: 30px;
font-style: normal;
font-weight: 400;
line-height: normal;`;


function App() {
  const [hoveredQuarter, setHoveredQuarter] = useState("");
  const [hoverCategories, setHoverCategories] = useState([]);
  const [selectedName, setSelectedName] = useState("all");

  const allCounts = [...dataAll, ...dataRaqqa, ...dataMosul].flatMap(d => d.categories.map(c => c.count));
  const minCount = Math.min(...allCounts);
  const maxCount = Math.max(...allCounts);

  const sizeScale = scaleLinear()
    .domain([minCount, maxCount])
    .range([8, 150]);

  const selectedData = selectedName === "mosul" ? dataMosul : selectedName === "raqqa" ? dataRaqqa : dataAll;
  const filteredData = selectedData.map(obj => {
    const temp = obj.categories.map((obj) => hoverCategories.length === 0 || hoverCategories.includes(obj.name) ? obj : { name: obj.name, count: 0 });
    return {
      quarter: obj.quarter,
      categories: temp
    };
  });

  const handleSelectName = (name) => {
    if (selectedName === name) {
      setSelectedName("all");
    } else {
      setSelectedName(name);
    }
  };
  return (
    <AppContainer className="App">

      <AppHeader>
        <img height={700} src={backgroudUrl} alt="map" />
        <Circle></Circle>
        <HeaderText>
          <ArticleTitle>The Rise of “Dual-Use” Objects in War </ArticleTitle>
        </HeaderText>

      </AppHeader>



      <ContentContainer>
        <MapContainer>
          <img height={700} src={mapUrl} alt="map" />
          <IndicatorContainer style={{ opacity: selectedName === "raqqa" ? .5 : 1 }} onClick={() => handleSelectName("mosul")}>
            <MosulIndicator height={70} width={40} src={Vector} alt="map" />
            <MosulText>Mosul</MosulText>
          </IndicatorContainer>
          <IndicatorContainer style={{ opacity: selectedName === "mosul" ? .5 : 1 }} onClick={() => handleSelectName("raqqa")}>
            <RaqqaIndicator height={70} width={40} src={Vector} alt="map" />
            <RaqqaText>Raqqa</RaqqaText>
          </IndicatorContainer>
        </MapContainer>
        <RightPanel>
          <LineChart setHoverCategories={setHoverCategories} hoverCategories={hoverCategories} hoveredQuarter={hoveredQuarter} setHoveredQuarter={setHoveredQuarter} data={filteredData} name={selectedName === "mosul" ? "Mosul, Iraq" : selectedName === "raqqa" ? "Raqqa, Syria" : ""} />
          <CategoryBubbleChart hoverCategories={hoverCategories} setHoveredQuarter={setHoveredQuarter} hoveredQuarter={hoveredQuarter} data={filteredData} sizeScale={sizeScale} />
        </RightPanel>
      </ContentContainer>
    </AppContainer>
  );
}

export default App;
